import React, { Component } from "react";

class GraphGenerator extends Component {
  constructor() {
    super();
    this.state = {
      graph: {},
      nodeCount: 0
    };
  }

  generateGraph = (nodeCount, isDirected) => {
    let result = fetch(`http://localhost:5000/create-graph`, {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({ nodeCount: nodeCount, isDirected: isDirected })
    });
    let response = result.then(response => response.json());
    response.then(data =>
      this.setState({
        graph: data.graph
      })
    );
  };

  handleChange = e => {

  }
  render() {
    return (
      <div>
        <input type="number" id="nodeCount"></input>
        <button>Generate Graph</button>
      </div>
    );
  }
}

export default GraphGenerator;
