import React, { Component } from "react";
import "./GraphGenerator.css";

let nodePositions = {};
let graphEdges = [];

class GraphGenerator extends Component {
  constructor() {
    super();
    this.state = {
      graph: {},
      nodeCount: 0,
      isDirected: false
    };
  }

  generateGraph = () => {
    const { nodeCount, isDirected } = this.state;
    let result = fetch(`http://localhost:5000/create-graph`, {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({ nodeCount: nodeCount, isDirected: isDirected })
    });

    let response = result.then(response => response.json());
    response.then(data =>
      this.setState({
        graph: data.graph
      })
    );
  };

  handleChange = e => {
    this.setState({
      [e.target.id]: e.target.value
    });
  };

  displayNodes = node => {
    const colors = ["#744FC6", "#4F86C6", "#4FB0C6", "#379392"];
    const randomColor = colors[Math.floor(Math.random() * colors.length)];

    return (
      <div
        className="node"
        key={node}
        id={node}
        style={{ backgroundColor: randomColor }}
      >
        {node}
      </div>
    );
  };

  displayEdges = (start, end) => {
    console.log("NODE POSITIONS");
    console.log(nodePositions);
    console.log(graphEdges);

    graphEdges.push(
      <svg width="500" height="500">
        <line
          x1={nodePositions[start][0]}
          y1={nodePositions[start][1]}
          x2={nodePositions[end][0]}
          y2={nodePositions[end][1]}
          stroke="black"
        />
      </svg>
    );
    // return (
    //   <svg width="500" height="500">
    //     <line
    //       x1={nodePositions[start][0]}
    //       y1={nodePositions[start][1]}
    //       x2={nodePositions[end][0]}
    //       y2={nodePositions[end][1]}
    //       stroke="black"
    //     />
    //   </svg>
    // );
  };

  getNodePositions = node => {
    let offset = document.getElementById(node).getBoundingClientRect();
    Object.assign(nodePositions, { [node]: [offset.x, offset.y] });
  };

  componentDidUpdate(prevProps, prevState) {
    const { nodes, edges } = this.state.graph;

    if (nodes !== undefined) {
      nodePositions = {};
      nodes.map(node => {
        return this.getNodePositions(node);
      });
    }
    console.log("POSITIONS");
    console.log(nodePositions);
    if (edges !== undefined) {
      graphEdges = [];
      edges.map(([start, end]) => {
        return this.displayEdges(start, end);
      });
      console.log("edges inside componentdidupdate");
      console.log(graphEdges);
    }
  }

  render() {
    const { nodes } = this.state.graph;
    const nodeList = nodes
      ? nodes.map(node => {
          return this.displayNodes(node);
        })
      : null;
    return (
      <div>
        <input
          type="number"
          id="nodeCount"
          onChange={this.handleChange}
        ></input>
        <button onClick={this.generateGraph}>Generate Graph</button>
        <div className="nodes">{nodeList}</div>
        <div className="edges">{graphEdges}</div>
        {console.log("render")}
        {console.log(nodePositions)}
        {/* <svg className="edges" width="500" height="500">
          <line
            className="edges"
            x1="72.390625"
            y1="37"
            x2="302.1875"
            y2="37"
            stroke="black"
          />
        </svg> */}
      </div>
    );
  }
}

export default GraphGenerator;
